import React, { useState } from 'react'
import '../Styles/EnrollmentForm.css'
import LoginInput from '../Components/LoginInput'

export default function SignupSection() {

  const [credentails] = useState("ad")

  //Function to hadle sign up
  const handleSignup = () =>{
    return 0
  }

  const onChange = () =>{
    return 0
  }

  const changeUser = () =>{
    return 0
  }

  const inputs = [
        {
            id:1,
            name:'username',
            type:'text',
            placeholder:'Username',
            label:'Username:',
            required:true,
        },
        {
            id:2,
            name:'password',
            type:'password',
            placeholder:'Password',
            label:'Password:',
            // errorMessage:'Password doesnt match username',
            required:true,
        },
        {
            id:2,
            name:'cpassword',
            type:'password',
            placeholder:'Confirm Password',
            label:'Confirm Password:',
            // errorMessage:'Password doesnt match username',
            required:true,
        }
    ];
    
  return (
    <div>
      <div className='section'>
        <div className='login-form'>
        <h2 className='message'>Please Enter the Credentials</h2>
        <form onSubmit={handleSignup}>
        {
            inputs.map((input) => (
                <LoginInput key={input.id} {...input} value={credentails[input.name]} onChange={onChange} />
            ))
        }
            <select name="usertype" className='user-select' onChange={changeUser}>
                <option className='opt'>Student</option>
                <option className='opt'>Teacher</option>
                <option className='opt'>Admin</option>
            </select>
            <button type='submit'>Log In</button>
        </form>
    </div>
      </div>
    </div>
  )
}
