import React from 'react'

export default function NoticeSection(props) {
        return (
        <> 
        {
            (props.notices).map((value,index)=>{
            return(
                <div className="accordion" id="accordionExample" style={{width:'90%'}}>
                <div className="accordion-item">
                <h2 class="accordion-header" id={"heading".concat(value.number)}>
                    <button className="accordion-button collapsed" 
                    type="button" 
                    data-bs-toggle="collapse" 
                    data-bs-target={"#collapse".concat(value.number)}
                    aria-expanded="true" 
                    aria-controls={"collapse".concat(value.number)}>
                    {value.title}
                    <div 
                    style={
                        {paddingLeft:'1rem',fontSize:'0.7rem',color:'var(--clr--grey)'}}>
                        {value.date}
                    </div>
                    </button>
                </h2>
                    <div id={"collapse".concat(value.number)} className="accordion-collapse collapse show" 
                    aria-labelledby={"heading".concat(value.number)}
                    data-bs-parent="#accordionExample">
                        <div className="accordion-body">
                            {value.description}
                        </div>
                    </div>
                </div>
                </div>
                )
            })
        }
        </>
    )
}

<div class="accordion" id="accordionExample">
  
  <div class="accordion-item">
    <h2 class="accordion-header" id="headingTwo">
      <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#collapseTwo" aria-expanded="false" aria-controls="collapseTwo">
        Accordion Item #2
      </button>
    </h2>
    <div id="collapseTwo" class="accordion-collapse collapse" aria-labelledby="headingTwo" data-bs-parent="#accordionExample">
      <div class="accordion-body">
        <strong>This is the second item's accordion body.</strong> It is hidden by default, until the collapse plugin adds the appropriate classes that we use to style each element. These classes control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables. It's also worth noting that just about any HTML can go within the <code>.accordion-body</code>, though the transition does limit overflow.
      </div>
    </div>
  </div>
</div>
