import React from 'react'
import "../Styles/NoticeResultSection.css"

export default function NoticeSection() {
    const noticeInfo = [
    {
        "_id": "645d025e73ad5d2c65897be0",
        "title": "class 10 School opens after winter vacation.",
        "usernotice": "School will be opening on sunday after 2 weeks.",
        "date": "2023-05-11T14:57:34.345Z",
        "__v": 0
    },
    {
        "_id": "645dda7d17c24f814757de2e",
        "title": "This is aasdasda new Notice",
        "usernotice": "Please dont be absent.yes yes yes yes",
        "date": "2023-05-12T06:19:41.163Z",
        "__v": 0
    },
    {
        "_id": "645dda9317c24f814757de30",
        "title": "todays note 4th i gues This is aasdasda new Notice",
        "usernotice": "Please dont be absent.yes yes yes yes",
        "date": "2023-05-12T06:20:03.753Z",
        "__v": 0
    }
    ]
    const displaydate= (date) =>{
        const d = new Date(date)
    } 
        
        return (
        <> 
        {
            noticeInfo.map((value,index)=>{
            return(
                
                <div className="accordion" id="accordionExample" style={{width:'95%'}}>
                <div className="accordion-item">
                <h2 class="accordion-header" id={"heading".concat(index)}>
                    <button className="accordion-button collapsed" 
                    type="button" 
                    data-bs-toggle="collapse" 
                    data-bs-target={"#collapse".concat(index)}
                    aria-expanded="false" 
                    aria-controls={"collapse".concat(index)}>
                    {value.title}
                    <div 
                    style={
                        {paddingLeft:'1rem',fontSize:'0.7rem',color:'var(--clr--grey)'}}>
                        {displaydate(value.date)}
                    </div>
                    <div className='actionitems'>
                        <i className='fa-solid fa-user-plus'></i>
                        <i className='fa-solid fa-message'></i>
                    </div>
                    </button>
                </h2>
                    <div id={"collapse".concat(index)} className="accordion-collapse collapse" 
                    aria-labelledby={"heading".concat(index)}
                    data-bs-parent="#accordionExample">
                        <div className="accordion-body">
                            {value.usernotice}
                        </div>
                    </div>
                </div>
                </div>
                )
            })
        }
        </>
    )
}
