import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';

export default function GroupResult() {

    const navigate = useNavigate()
    useEffect(()=>{
        if(!localStorage.getItem('token')){
            navigate('/login');
        }
    },[])

    //Alert State
    const [created, setCreated] = useState(false);
    const [notcreate, setNotcreate] = useState(false);

    

    const readExcel = (file)=>{
        
        const promise = new Promise((resolve, reject)=>{
            const fileReader = new FileReader(file);
            fileReader.readAsArrayBuffer(file)

            fileReader.onload = (e)=>{
                const bufferArray = e.target.result;

                const wb = XLSX.read(bufferArray,{type:'buffer'});

                const wsname = wb.SheetNames[0];
                const ws = wb.Sheets[wsname];
                const data = XLSX.utils.sheet_to_json(ws);
                resolve(data);
            };
            fileReader.onerror = ((error)=>{
                reject(error);
            })
        })

        promise.then((data)=>{
            
            // For columns
            const columnNames = Object.keys(data[0])
            let columns = [{
                field: "action",
                headerName: "Action",
                sortable: false,
                renderCell: ({ row }) =>
                <button className='action-btn' onClick={()=>{return createResult(row)}}><i className="fa-solid fa-user-plus"></i>Publish</button>,
                width: 150
            }];
            
            columnNames.forEach((value) => {
                columns.push({
                    field: value, 
                    headerName: value, 
                    width: 100,
                    sortable: false 
                })
            });
            setColumns(columns);

            let rows = [];

            data.forEach((value,index) => {
                rows.push({
                    create : 'Create',
                    id: ++index,
                    ...value
                });
            });
            setRows(rows);
            
        })
    }
    const [columns, setColumns] = useState([]);
    const [rows, setRows] = useState([]);

    return (
        <div className='innerHero'>
        <div className='formPart'>
            <label className='excelLabel'>Upload An Excel File</label>
                <input 
                className='custom-file-input'
                type="file" 
                onChange={(e)=>{
                    const file = e.target.files[0];
                    readExcel(file);
                }}
                accept='.xlsx, .xls'
                />
            </div>
        </div>
    )
}
