            info.push({
                id: ++index, 
                name: value.name, 
                gender: value.gender, 
                phone: value.phone, 
                dob: value.dob,
                address: value.address,
                grade: value.grade,
            })
        })
        console.log('thissssss')
    }
    */
   const info = []
    useEffect(() => {
        info = teachersinfo.map((value, index) => ({
        id: ++index,
        name: value.name,
        gender: value.gender,
        phone: value.phone,
        dob: value.dob,
        address: value.address,
        grade: value.grade,
        }
    ));
    },[teachersinfo]);
    
    


    const rows = [
    {   
        id: 1, 
        name: teachersinfo[0].name, 
        gender: teachersinfo[0].gender, 
        phone: teachersinfo[0].phone, 
        dob: teachersinfo[0].dob,
        address: teachersinfo[0].address,
        grade: teachersinfo[0].grade,
    },
    {
        id: 2, 
        name: teachersinfo[0].name, 
        gender: teachersinfo[0].gender, 
        phone: teachersinfo[0].phone, 
        dob: teachersinfo[0].dob,
        address: teachersinfo[0].address,
        grade: teachersinfo[0].grade,
    }
    ];
    console.log('this')
    return (
    <>
        <div style={{ height: 600, width: '95%' }}>
        <DataGrid
            rows={teachersinfo}
            columns={columns}
            pageSize={10}
            rowsPerPageOptions={[5]}
        />
        </div>
    </>
    );
    
}
