import React, { useContext } from 'react';
import { DataGrid } from '@mui/x-data-grid';
import UserContext from '../../context/user/UserContext';

const columns = [
    { field: 'id', headerName: 'ID', width: 90 },
    { field: 'name', headerName: 'Full Name', width: 150 },
    
    {
        field: 'gender',
        headerName: 'Age',
        type: 'number',
        width: 100,
    },
    {
        field: 'fullName',
        headerName: 'Full name',
        description: 'This column has a value getter and is not sortable.',
        sortable: false,
        width: 160,
        valueGetter: (params) =>
        `${params.row.firstName || ''} ${params.row.lastName || ''}`,
    },
    {
        field:'action',
        headerName: 'Actions',
        description: 'Permanently delete the record.',
        sortable: false,
        width: 150

    }
    ];



export default function TableArea() {

    const context  = useContext(UserContext);
    const {teachersinfo,setTeachersinfo} = context;
    console.log(teachersinfo)

    const rows = [
    { id: 1, name: teachersinfo.name, gender: teachersinfo.gender, phone: teachersinfo.phone, dob: 35},
    
    ];

    return (
    <>
        <div style={{ height: 600, width: '95%' }}>
        <DataGrid
            rows={rows}
            columns={columns}
            pageSize={10}
            rowsPerPageOptions={[5]}
        />
        </div>
    </>
    );
    
}
