import React,{useState} from 'react'
import * as XLSX from 'xlsx'
import { DataGrid } from '@mui/x-data-grid';

export default function GroupAccount() {
    const [columns, setColumns] = useState([]);
    const [rows, setRows] = useState([]);

    const readExcel = (file)=>{
        
        const promise = new Promise((resolve, reject)=>{
            const fileReader = new FileReader(file);
            fileReader.readAsArrayBuffer(file)

            fileReader.onload = (e)=>{
                const bufferArray = e.target.result;

                const wb = XLSX.read(bufferArray,{type:'buffer'});

                const wsname = wb.SheetNames[0];
                const ws = wb.Sheets[wsname];
                const data = XLSX.utils.sheet_to_json(ws);
                resolve(data);
            };
            fileReader.onerror = ((error)=>{
                reject(error);
            })
        })

        promise.then((data)=>{
            //console.log(data);
            
            // For columns
            const columnNames = Object.keys(data[0])
            let columns = [{
                field: "action",
            headerName: "Action",
            sortable: false,
            renderCell: ({ row }) =>
                <StudentModalBox
                heading = "Delete Student Account?"
                question = {"Are you sure you want to delete " + row.name +  "'s account?"}
                action = "Delete"
                todo = 'delProfile'
                userid = {row.idreal}
                />,
                width: 70
            }];
            
            columnNames.forEach((value) => {
                columns.push({
                    field: value, headerName: value, width: 100,
                    sortable: false 
                })
            });

            console.log(columns);
            setColumns(columns);

            let rows = [];

            data.forEach((value,index) => {
                rows.push({
                    cerate : 'Create',
                    id: ++index,
                    ...value
                });
            });
            setRows(rows);
            /*
            data.map((value,index)=>{
                console.log(value)
            })
            const columns = [
            
            { field: 'id', headerName: 'ID', width: 50 },

            { field: 'name', headerName: 'Full Name', width: 150,},
            {
                field: 'gender',
                headerName: 'Gender',
                width: 100,
            },
            {
                field: 'phone',
                headerName: 'Phone',
                description: 'The students phone number is here.',
                width: 160,
                sortable:false
            },
            {
                field: 'address',
                headerName: 'Address',
                description: 'The students address is here.',
                width: 160,
            },
            {
                field: 'fathername',
                headerName: "Father's Name",
                description: " Father's name is here.",
                width: 160,
            }
            ];*/
        })
    }

    return (
        <div id='innerHero'>
        <div className='formPart'>
            <input 
            type="file" 
            onChange={(e)=>{
                const file = e.target.files[0];
                readExcel(file);
            }}
            accept='.xlsx, .xls'
            />
        </div>
        <div className='dataPart'>
            <DataGrid
                rows={rows}
                columns={columns}
                initialState={{
                pagination: {
                    paginationModel: { page: 0, pageSize: 10 },
                },
                }}
                pageSizeOptions={[5, 10, 50]}
            />
        </div>
        </div>
    )
}
