import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import "../Styles/NoticeResultSection.css";
import UpdateNoticeForm from './UpdateNoticeForm';

export default function UpdateSection() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
        <Button className='actionicon iconedit' onClick={handleShow}>
            <i className="fa-solid fa-pen-to-square" ></i>Edit
        </Button>

        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
            <Modal.Title>Edit Notice üìù</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <h4>You can edit here</h4>
                <UpdateNoticeForm/>
            </Modal.Body>
            <Modal.Footer>
            <Button className="additem-btn icondelete" onClick={handleClose}>
                Close
            </Button>
            <Button className="additem-btn iconedit" onClick={handleClose}>
                Update Notice
            </Button>
            </Modal.Footer>
        </Modal>
    </>
    );
}
