import React, { useContext, useEffect } from 'react';
import { DataGrid } from '@mui/x-data-grid';
import UserContext from '../../context/user/UserContext';

const columns = [
    { field: 'id', headerName: 'ID', width: 90 },
    { field: 'name', headerName: 'Full Name', width: 150 },
    
    {
        field: 'gender',
        headerName: 'Gender',
        width: 100,
    },
    {
        field: 'phone',
        headerName: 'Phone',
        description: 'The Teachers phone number is here.',
        width: 160,
        sortable:false
    },
    {
        field: 'dob',
        headerName: 'Date of Birth',
        description: 'The Teachers date of birth is here.',
        width: 160,
    },
    {
        field: 'address',
        headerName: 'Address',
        description: 'The Teachers address is here.',
        width: 160,
    },
    {
        field: 'grade',
        headerName: 'Grade',
        description: 'The grade is here.',
        width: 160,
    },
    {
        field:'action',
        headerName: 'Actions',
        description: 'Permanently delete the record.',
        sortable: false,
        width: 150

    }
    ];



export default function TableArea() {

    const context  = useContext(UserContext);
    const {teachersinfo,setTeachersinfo} = context;
    
    let info = [];
    
    const showTeacherList = () =>{
        teachersinfo.map((value,index)=>{
            info.push({
                id: ++index, 
                name: value.name, 
                gender: value.gender, 
                phone: value.phone, 
                dob: value.dob,
                address: value.address,
                grade: value.grade,
            })
        })
        console.log(JSON.stringify(info,
      null, 4))
    }

    useEffect(()=>{
        showTeacherList();
    },[]);
    
    const rows = [
    {   
        id: 1, 
        name: teachersinfo[0].name, 
        gender: teachersinfo[0].gender, 
        phone: teachersinfo[0].phone, 
        dob: teachersinfo[0].dob,
        address: teachersinfo[0].address,
        grade: teachersinfo[0].grade,
    }
    ];

    return (
    <>
        <div style={{ height: 600, width: '95%' }}>
        <DataGrid
            rows={rows}
            columns={columns}
            pageSize={10}
            rowsPerPageOptions={[5]}
        />
        </div>
    </>
    );
    
}
